public class Calculator {

	public int substract(int a, int b) {
		// TODO Auto-generated method stub
		return a - b;
	}
	
	public int add(int a, int b) {
		// TODO Auto-generated method stub
		return a + b;
	}
	
	public int multiple(int a, int b) {
		return a * b;
	}
	
	public int divide(int a, int b) {
		return a / b;
	}
}
